package contract

import (
	"github.com/ethereum/go-ethereum/accounts/abi"
	"github.com/ethereum/go-ethereum/accounts/abi/bind"
	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/core/types"
	"math/big"
	"strings"
)

type EmitEvent struct {
	Tuid        *big.Int
	EthAddress  [20]byte
	OrbsAddress [20]byte
	Value       *big.Int
}

const EmitEventAbi = `
[
    {
      "inputs": [],
      "payable": false,
      "stateMutability": "nonpayable",
      "type": "constructor"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": true,
          "name": "tuid",
          "type": "uint256"
        },
        {
          "indexed": true,
          "name": "ethAddress",
          "type": "address"
        },
        {
          "indexed": true,
          "name": "orbsAddress",
          "type": "bytes20"
        },
        {
          "indexed": false,
          "name": "value",
          "type": "uint256"
        }
      ],
      "name": "TransferredOut",
      "type": "event"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": false,
          "name": "foo",
          "type": "string"
        }
      ],
      "name": "AnotherEvent",
      "type": "event"
    },
    {
      "constant": false,
      "inputs": [
        {
          "name": "tuid",
          "type": "uint256"
        },
        {
          "name": "ethAddress",
          "type": "address"
        },
        {
          "name": "orbsAddress",
          "type": "bytes20"
        },
        {
          "name": "value",
          "type": "uint256"
        }
      ],
      "name": "transferOut",
      "outputs": [],
      "payable": false,
      "stateMutability": "nonpayable",
      "type": "function"
    }
  ]`

const EmitEventBin = ""

func DeployEmitEvent(auth *bind.TransactOpts, backend bind.ContractBackend) (common.Address, *types.Transaction, error) {
	parsed, err := abi.JSON(strings.NewReader(EmitEventAbi))
	if err != nil {
		return common.Address{}, nil, err
	}
	address, tx, _, err := bind.DeployContract(auth, parsed, common.FromHex(EmitEventBin), backend)
	if err != nil {
		return common.Address{}, nil, err
	}
	return address, tx, nil
}
